mapper.insert:
INSERT INTO food_store_star (
		STORE_SEQ
		, STORE_STAR

		, REG_ID
		, REG_DT


		, REVIEW

	)
VALUES (
	#{storeSeq}
	, #{storeStar}

		, #{regId}
		, sysdate


		, #{review}

)




INSERT INTO search_tag(TAG_NAME) 
	SELECT 
		#{tag} 
	FROM 
		DUAL 
	WHERE 
		NOT EXISTS 
			(
				SELECT TAG_NAME FROM search_tag WHERE TAG_NAME = #{tag}
			)
